def decompress1536(frame, section):
    quadplate = []
    for i in section:
        quadrant = frame[(frame['Row'] == i[0]) & (frame['Column'] == i[1])]
        quadplate.append(quadrant)
    oneplate = pd.concat(quadplate)
    return oneplate

